// Componente Line/Arrow
const DrawingLine = ({ line, isSelected, onSelect }) => {
  const dx = line.x2 - line.x1;
  const dy = line.y2 - line.y1;
  const angle = Math.atan2(dy, dx);
  const headLen = 12;

  return (
    <g onMouseDown={(e) => onSelect(line.id, e)} style={{ cursor: 'pointer' }}>
      <line
        x1={line.x1} y1={line.y1} x2={line.x2} y2={line.y2}
        stroke={line.color} strokeWidth={isSelected ? 4 : 3}
        strokeDasharray={line.dashed ? '10 5' : 'none'}
      />
      {line.hasArrow && (
        <polygon
          points={`${line.x2},${line.y2} ${line.x2 - headLen * Math.cos(angle - 0.4)},${line.y2 - headLen * Math.sin(angle - 0.4)} ${line.x2 - headLen * Math.cos(angle + 0.4)},${line.y2 - headLen * Math.sin(angle + 0.4)}`}
          fill={line.color}
        />
      )}
    </g>
  );
};

// Componente L치piz libre (Freehand) - siempre con punta de flecha
const FreehandLine = ({ line, isSelected, onSelect }) => {
  if (!line.points || line.points.length < 2) return null;
  
  // Crear path desde los puntos
  const pathData = line.points.reduce((acc, point, i) => {
    return acc + (i === 0 ? `M ${point.x} ${point.y}` : ` L ${point.x} ${point.y}`);
  }, '');

  // Calcular 치ngulo para la punta de flecha (칰ltimos 2 puntos)
  const lastPoint = line.points[line.points.length - 1];
  const prevPoint = line.points[line.points.length - 2];
  const dx = lastPoint.x - prevPoint.x;
  const dy = lastPoint.y - prevPoint.y;
  const angle = Math.atan2(dy, dx);
  const headLen = 12;

  return (
    <g onMouseDown={(e) => onSelect(line.id, e)} style={{ cursor: 'pointer' }}>
      <path
        d={pathData}
        stroke={line.color}
        strokeWidth={isSelected ? 4 : 3}
        strokeDasharray={line.dashed ? '10 5' : 'none'}
        fill="none"
        strokeLinecap="round"
        strokeLinejoin="round"
      />
      {/* Punta de flecha */}
      <polygon
        points={`${lastPoint.x},${lastPoint.y} ${lastPoint.x - headLen * Math.cos(angle - 0.4)},${lastPoint.y - headLen * Math.sin(angle - 0.4)} ${lastPoint.x - headLen * Math.cos(angle + 0.4)},${lastPoint.y - headLen * Math.sin(angle + 0.4)}`}
        fill={line.color}
      />
    </g>
  );
};

// Componente Flecha curva con punto de control arrastrable
const CurvedArrow = ({ line, isSelected, onSelect, onControlDrag }) => {
  const { x1, y1, x2, y2, cx, cy } = line;
  const controlX = cx ?? (x1 + x2) / 2;
  const controlY = cy ?? (y1 + y2) / 2 - 50;
  
  // Calcular 치ngulo para la punta de flecha (tangente al final de la curva)
  const dx = x2 - controlX;
  const dy = y2 - controlY;
  const angle = Math.atan2(dy, dx);
  const headLen = 12;

  const handleControlMouseDown = (e) => {
    e.stopPropagation();
    if (onControlDrag) {
      onControlDrag(line.id, e);
    }
  };

  return (
    <g onMouseDown={(e) => onSelect(line.id, e)} style={{ cursor: 'pointer' }}>
      <path
        d={`M ${x1} ${y1} Q ${controlX} ${controlY} ${x2} ${y2}`}
        stroke={line.color}
        strokeWidth={isSelected ? 4 : 3}
        strokeDasharray={line.dashed ? '10 5' : 'none'}
        fill="none"
        strokeLinecap="round"
      />
      <polygon
        points={`${x2},${y2} ${x2 - headLen * Math.cos(angle - 0.4)},${y2 - headLen * Math.sin(angle - 0.4)} ${x2 - headLen * Math.cos(angle + 0.4)},${y2 - headLen * Math.sin(angle + 0.4)}`}
        fill={line.color}
      />
      {/* Punto de control arrastrable cuando est치 seleccionado */}
      {isSelected && (
        <circle
          cx={controlX}
          cy={controlY}
          r="8"
          fill="#22c55e"
          stroke="white"
          strokeWidth="2"
          style={{ cursor: 'grab' }}
          onMouseDown={handleControlMouseDown}
        />
      )}
    </g>
  );
};

// Componente Rect치ngulo editable
const EditableRect = ({ shape, isSelected, onSelect, onResize, activeTool }) => {
  const handleMouseDown = (e) => {
    // Solo capturar clic si estamos en modo select
    if (activeTool === 'select') {
      onSelect(shape.id, e);
    }
    // Si no, dejar que el evento pase al canvas
  };
  
  return (
    <g 
      onMouseDown={handleMouseDown} 
      style={{ cursor: activeTool === 'select' ? 'move' : 'crosshair', pointerEvents: activeTool === 'select' ? 'auto' : 'none' }}
    >
      <rect
        x={shape.x}
        y={shape.y}
        width={shape.width}
        height={shape.height}
        fill={shape.fill || 'transparent'}
        stroke={shape.color}
        strokeWidth={isSelected ? 4 : 3}
        strokeDasharray={shape.dashed ? '10 5' : 'none'}
        rx="2"
        style={{ pointerEvents: activeTool === 'select' ? 'auto' : 'none' }}
      />
      {isSelected && (
        <>
          {/* Handles de redimensi칩n */}
          <circle cx={shape.x} cy={shape.y} r="6" fill="#22c55e" style={{ cursor: 'nw-resize' }} 
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'nw', e); }} />
          <circle cx={shape.x + shape.width} cy={shape.y} r="6" fill="#22c55e" style={{ cursor: 'ne-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'ne', e); }} />
          <circle cx={shape.x} cy={shape.y + shape.height} r="6" fill="#22c55e" style={{ cursor: 'sw-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'sw', e); }} />
          <circle cx={shape.x + shape.width} cy={shape.y + shape.height} r="6" fill="#22c55e" style={{ cursor: 'se-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'se', e); }} />
        </>
      )}
    </g>
  );
};

// Componente C칤rculo/Elipse editable
const EditableEllipse = ({ shape, isSelected, onSelect, onResize, activeTool }) => {
  const cx = shape.x + shape.rx;
  const cy = shape.y + shape.ry;
  
  const handleMouseDown = (e) => {
    if (activeTool === 'select') {
      onSelect(shape.id, e);
    }
  };
  
  return (
    <g 
      onMouseDown={handleMouseDown} 
      style={{ cursor: activeTool === 'select' ? 'move' : 'crosshair', pointerEvents: activeTool === 'select' ? 'auto' : 'none' }}
    >
      <ellipse
        cx={cx}
        cy={cy}
        rx={shape.rx}
        ry={shape.ry}
        fill={shape.fill || 'transparent'}
        stroke={shape.color}
        strokeWidth={isSelected ? 4 : 3}
        strokeDasharray={shape.dashed ? '10 5' : 'none'}
        style={{ pointerEvents: activeTool === 'select' ? 'auto' : 'none' }}
      />
      {isSelected && (
        <>
          {/* Handles de redimensi칩n */}
          <circle cx={cx - shape.rx} cy={cy} r="6" fill="#22c55e" style={{ cursor: 'ew-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'w', e); }} />
          <circle cx={cx + shape.rx} cy={cy} r="6" fill="#22c55e" style={{ cursor: 'ew-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'e', e); }} />
          <circle cx={cx} cy={cy - shape.ry} r="6" fill="#22c55e" style={{ cursor: 'ns-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 'n', e); }} />
          <circle cx={cx} cy={cy + shape.ry} r="6" fill="#22c55e" style={{ cursor: 'ns-resize' }}
            onMouseDown={(e) => { e.stopPropagation(); onResize(shape.id, 's', e); }} />
        </>
      )}
    </g>
  );
};

